mapper.select:
/* Care.selectList */
SELECT CA_SEQ caSeq , DATE_FORMAT(CA_RGST_DT,'%Y.%m.%d') caRgstDt , CA_RGST_SEQ caRgstSeq , CA_RVSE_DT caRvseDT , CA_RVSE_SEQ caRvseSeq , F_USER_NM(CA_RGST_SEQ) caName , CA_TITLE caTitle , CA_CONT caCont , CA_ATCH_FILE_SEQ caAtchFileSeq , CA_DAT_SEQ caDatSeq , CA_DAT_RGST_SEQ caDatRgstSeq , F_USER_NM(CA_DAT_RGST_SEQ) caDatName , CA_DAT_RVSE_SEQ caDatRvseSeq , CA_DAT_RVSE_DT caDatRvseDT , CA_DAT_CONT caDatCont , CA_DAT_ATCH_FILE_SEQ caDatAtchFileSeq FROM T_CARE WHERE CA_USE_YN='Y'
ORDER BY CA_SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
      /* Care.insertContents */

           INSERT INTO T_CARE(
	              CA_SEQ   
	            , CA_TITLE 
	            , CA_CONT  
	            , CA_ATCH_FILE_SEQ 
	            , CA_RGST_SEQ
   	         )
         VALUES (
             (SELECT IFNULL(MAX(CA.CA_SEQ)+1,1) FROM T_CARE CA)
	            , #{caTitle}  
	            , #{caCont}   
	            , #{caAtchFileSeq}
	            , #{loginSeq}
	         )

mapper.update:
      /* Care.updateContents */

           UPDATE T_CARE     
	          SET CA_DAT_RGST_DT				= NOW()
	            , CA_DAT_RVSE_DT				= NOW()
	            , CA_DAT_SEQ					= (SELECT IFNULL(MAX(CA.CA_DAT_SEQ)+1,1) FROM T_CARE CA)
	            , CA_DAT_RGST_SEQ				= #{loginSeq}
	            , CA_DAT_CONT 					= #{caDatCont}
	            , CA_DAT_ATCH_FILE_SEQ 			= #{caDatAtchFileSeq}
	        WHERE CA_SEQ = #{caSeq}

      /* Care.updateDatContents */

           UPDATE T_CARE     
	          SET CA_DAT_RGST_DT				= NOW()
	            , CA_DAT_RVSE_DT				= NOW()
	            , CA_DAT_SEQ					= (SELECT IFNULL(MAX(CA.CA_DAT_SEQ)+1,1) FROM T_CARE CA)
	            , CA_DAT_RGST_SEQ				= #{loginSeq} 
	            , CA_DAT_CONT 					= #{caDatCont}
	            , CA_DAT_ATCH_FILE_SEQ 			= #{caDatAtchFileSeq}
	        WHERE CA_SEQ = #{caSeq}

/* Care.deleteContents */

    UPDATE T_CARE
       SET CA_USE_YN='N'
     WHERE CA_SEQ=#{caSeq}
