mapper.select:
/* Ctwo.selectList */
SELECT CT_SEQ ctSeq , DATE_FORMAT(CT_RGST_DT,'%Y.%m.%d') ctRgstDt , CT_RGST_SEQ ctRgstSeq , CT_RVSE_DT ctRvseDT , CT_RVSE_SEQ ctRvseSeq , F_USER_NM(CT_RGST_SEQ) ctName , CT_TITLE ctTitle , CT_CONT ctCont , CT_ATCH_FILE_SEQ ctAtchFileSeq , CT_DAT_SEQ ctDatSeq , CT_DAT_RGST_SEQ ctDatRgstSeq , F_USER_NM(CT_RGST_SEQ) ctDatName , CT_DAT_RVSE_SEQ ctDatRvseSeq , CT_DAT_RVSE_DT ctDatRvseDT , CT_DAT_TITLE ctDatTitle , CT_DAT_CONT ctDatCont , CT_DAT_ATCH_FILE_SEQ ctDatAtchFileSeq FROM T_CTWO WHERE CT_USE_YN='Y'
ORDER BY CT_SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
      /* Ctwo.insertContents */

           INSERT INTO T_CTWO(
	              CT_SEQ   
	            , CT_TITLE 
	            , CT_CONT  
	            , CT_ATCH_FILE_SEQ 
	            , CT_RGST_SEQ
   	         )
         VALUES (
             (SELECT IFNULL(MAX(CT.CT_SEQ)+1,1) FROM T_CTWO CT)
	            , #{ctTitle}  
	            , #{ctCont}   
	            , #{ctAtchFileSeq}
	            , #{loginSeq}
	         )

mapper.update:
      /* Ctwo.updateContents */

           UPDATE T_CTWO     
	          SET CT_TITLE 						= #{ctTitle} 
           		, CT_CONT 						= #{ctCont}
	            , CT_RVSE_DT 					= NOW()
                , CT_RVSE_SEQ					= #{loginSeq}
                , CT_TITLE						= #{ctTitle}
            	, CT_DAT_SEQ 					= #{ctDatRgstDt}
           		, CT_DAT_RGST_SEQ 				= #{ctDatRgstSeq}
	            , CT_DAT_RVSE_SEQ 				= #{loginSeq}
	            , F_USER_NM(CT_DAT_RGST_SEQ) 	= #{ctDatName}
	            , CT_DAT_RVSE_DT 				= #{}
	            , CT_DAT_TITLE 					= #{ctDatTitle}
	            , CT_DAT_CONT 					= #{ctDatCont}
	            , CT_DAT_ATCH_FILE_SEQ 			= #{ctDatAtchFileSeq}
	        WHERE CT_SEQ = #{ctSeq}

/* Ctwo.deleteContents */

    UPDATE T_CTWO
       SET CT_USE_YN='N'
     WHERE CT_SEQ=#{ctSeq}
