mapper.select:
/* Member.selectList */
SELECT ME_SEQ meSeq , DATE_FORMAT(ME_RGST_DT,'%Y.%m.%d') meRgstDt , ME_RGST_SEQ meRgstSeq , ME_CNUM meCnum , ME_MNAM meMnam , ME_CALL meCall , ME_BUSU meBusu , ME_LEVE meLeve , ME_STADATE meStaDate , ME_ENDDATE meEndDate , ME_VAC meVac , ME_TOTAL meTotaL , ME_ADRE meAdre , ME_GOOD meGood , ME_YEAR meYear , ME_MAIL meMail , ME_TID meTid , ME_CONT meCont FROM T_MEMBER WHERE ME_USE_YN='Y'
ORDER BY ME_SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
		/* Member.insertContents */

         INSERT INTO T_MEMBER(
              ME_SEQ 
            , ME_RGST_SEQ 
            , ME_CNUM 
            , ME_MNAM
            , ME_CALL 
            , ME_BUSU 
            , ME_LEVE
            , ME_STADATE
            , ME_ENDDATE 							
            , ME_VAC 							
            , ME_TOTAL 							
            , ME_ADRE 							
            , ME_GOOD 							
            , ME_YEAR 							
            , ME_MAIL
            , ME_TID 
            , ME_CONT
         )
         VALUES (
             (SELECT IFNULL(MAX(ME.ME_SEQ)+1,1) FROM T_MEMBER ME)
            , #{loginSeq}
            , #{meCnum}
            , #{meMnam}
            , #{meCall}
            , #{meBusu}
            , #{meLeve}
            , #{meStaDate}
            , #{meEndDate}
            , #{meVac}
            , #{meTotaL}
            , #{meAdre}
            , #{meGood}
            , #{meYear}
            , #{meMail}
            , #{meTid}
            , #{meCont}
         )

mapper.update:
		/* Member.updateContents */

         UPDATE T_MEMBER     
          SET 
     		  ME_RGST_SEQ = #{meRgstSeq}	          
            , ME_CNUM = #{meCnum} 
            , ME_MNAM = #{meMnam}
            , ME_CALL  = #{meCall}
            , ME_BUSU  = #{meBusu}
            , ME_LEVE = #{meLeve}
            , ME_STADATE = #{meStaDate}
            , ME_ENDDATE = #{meEndDate}
            , ME_VAC = #{meVac}
            , ME_TOTAL = #{meTotaL}
            , ME_ADRE = #{meAdre}
            , ME_GOOD = #{meGood}
            , ME_YEAR = #{meYear}
            , ME_MAIL = #{meMail}
            , ME_TID = #{meTid}
            , ME_CONT = #{meCont}
            , ME_RVSE_DT = NOW()
         WHERE  ME_SEQ = #{meSeq}

		/* Member.deleteContents */

         UPDATE T_MEMBER
             SET
                 ME_USE_YN='N'
         WHERE ME_SEQ=#{meSeq}
