mapper.select:
/* Movies.selectList */
SELECT TW_SEQ twSeq , DATE_FORMAT(TW_RGST_DT,'%Y.%m.%d') twRgstDt , TW_RGST_SEQ twRgstSeq , F_USER_NM(TW_RGST_SEQ) twName , TW_TITLE twTitle , TW_E_TITLE twETitle , TW_CODE twCode , TW_MAKE_YEAR twMakeYear , TW_NATIONAL twNational , TW_STADATE twStaDate , TW_ENDDATE twEndDate , TW_TYPE twType , TW_GENRE twGenre , TW_DIRECTOR twDirector , TW_COMPANY twCompany , TW_ACTOR twActor , TW_TCOM twTcom , TW_LATI twLati , TW_CONT twCont , TW_ATCH_FILE_SEQ twAtchFileSeq , TW_NAME twRgstSeq FROM T_MOVIES WHERE TW_USE_YN='Y'
ORDER BY TW_SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
/* Movies.insertContents */

 INSERT INTO T_MOVIES(
        TW_SEQ 
      , TW_RGST_SEQ 
      , TW_TITLE 
      , TW_E_TITLE
      , TW_CODE 
      , TW_MAKE_YEAR 
      , TW_NATIONAL
      , TW_STADATE
      , TW_ENDDATE
      , TW_TYPE
      , TW_GENRE 							
      , TW_DIRECTOR 							
      , TW_COMPANY 							
      , TW_ACTOR 							
      , TW_TCOM 							
      , TW_LATI 							
      , TW_CONT
      , TW_ATCH_FILE_SEQ 
   )
   VALUES (
   	  (SELECT IFNULL(MAX(TW.TW_SEQ)+1,1) FROM T_MOVIES TW)
      , #{twRgstSeq}
      , #{twTitle}
      , #{twETitle}
      , #{twCode}
      , #{twMakeYear}
      , #{twNational}
      , #{twStaDate}
      , #{twEndDate}
      , #{twType}
      , #{twGenre}
      , #{twDirector}
      , #{twCompany}
      , #{twActor}
      , #{twTcom}
      , #{twLati}
      , #{twCont}
      , #{twAtchFileSeq}
   )

mapper.update:
/* Movies.updateContents */

   UPDATE T_MOVIES     
      SET TW_TITLE		 	= #{twTitle} 
        , TW_RVSE_DT 			= NOW()
        , TW_E_TITLE 			= #{twETitle}
        , TW_CODE  			= #{twCode}
        , TW_MAKE_YEAR 		= #{twMakeYear}
        , TW_NATIONAL 		= #{twNational}
        , TW_TYPE 			= #{twType}
        , TW_GENRE 			= #{twGenre}
        , TW_DIRECTOR 		= #{twDirector}
        , TW_COMPANY 			= #{twCompany}
        , TW_ACTOR 			= #{twActor}
        , TW_TCOM 			= #{twTcom}
        , TW_LATI 			= #{twLati}
        , TW_CONT 			= #{twCont}
        , TW_ATCH_FILE_SEQ	= #{twAtchFileSeq}
        , TW_NAME				= #{twRgstSeq}
    WHERE TW_SEQ 				= #{twSeq}

/* Movies.deleteContents */

   UPDATE T_MOVIES
      SET TW_USE_YN='N'
    WHERE TW_SEQ=#{twSeq}
